import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const res = await axios.post('http://localhost:4040/api/auth/login', form);
    const { token, role } = res.data;

    localStorage.setItem('token', token);
    localStorage.setItem('role', role); // ‚¨ÖÔ∏è Store role

    alert('Login successful');

    // üîÅ Redirect based on role
    if (role === 'admin') {
      navigate('/admin');
    } else {
      navigate('/');
    }
  } catch (err) {
    setError(err.response?.data?.message || 'Login failed');
  }
};


  return (
    <div style={styles.container}>
      <form onSubmit={handleSubmit} style={styles.form}>
        <h2>Login</h2>
        {error && <p style={styles.error}>{error}</p>}
        <input name="username" placeholder="Username" onChange={handleChange} required style={styles.input} />
        <input name="password" type="password" placeholder="Password" onChange={handleChange} required style={styles.input} />
        <button type="submit" style={styles.button}>Login</button>
      </form>
    </div>
  );
};

const styles = {
  container: { display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' },
  form: { display: 'flex', flexDirection: 'column', padding: 30, boxShadow: '0 0 10px #ccc', borderRadius: 10 },
  input: { marginBottom: 15, padding: 10, fontSize: 16 },
  button: { padding: 10, fontSize: 16 },
  error: { color: 'red', marginBottom: 10 }
};

export default LoginPage;
