import React from 'react';
import LeaveForm from '../components/LeaveForm';
import LeaveList from '../components/LeaveList';

const EmployeeDashboard = () => {
  const token = localStorage.getItem('token');

  return (
    <div style={styles.background}>
      <div style={styles.card}>
        <h2 style={styles.title}>Employee Dashboard</h2>
        <LeaveForm token={token} />
        <h3 style={styles.subtitle}>My Leaves</h3>
        <LeaveList token={token} />
      </div>
    </div>
  );
};

const styles = {
  background: {
    minHeight: '100vh',
    background: 'linear-gradient(to right, #6a11cb, #2575fc)', // same as login
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '40px',
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: '10px',
    padding: '30px',
    width: '90%',
    maxWidth: '600px',
    boxShadow: '0 4px 20px rgba(0,0,0,0.2)',
  },
  title: {
    marginBottom: '20px',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  subtitle: {
    marginTop: '30px',
    fontWeight: 'bold',
    textAlign: 'left',
  },
};

export default EmployeeDashboard;
