import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { applyLeave } from '../features/leaves/leaveSlice';

const LeaveForm = ({ token }) => {
  const dispatch = useDispatch();
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [reason, setReason] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(applyLeave({ data: { from, to, reason }, token }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="date" value={from} onChange={(e) => setFrom(e.target.value)} required />
      <input type="date" value={to} onChange={(e) => setTo(e.target.value)} required />
      <input type="text" value={reason} onChange={(e) => setReason(e.target.value)} placeholder="Reason" required />
      <button type="submit">Apply Leave</button>
    </form>
  );
};

export default LeaveForm;
