import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const token = localStorage.getItem('token'); // admin token
  const [leaves, setLeaves] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:4040/api/leave/all', {
      headers: { Authorization: `Bearer ${token}` }
    }).then((res) => setLeaves(res.data));
  }, [token]);

  const updateStatus = (id, status) => {
    axios.put(`http://localhost:4040/api/leave/${id}`, { status }, {
      headers: { Authorization: `Bearer ${token}` }
    }).then(() => window.location.reload());
  };

  return (
    <div>
      <h2>Admin Panel</h2>
      <ul>
        {leaves.map((l) => (
          <li key={l._id}>
            {l.user.username}: {l.from} to {l.to} - {l.reason} ({l.status})
            {l.status === 'Pending' && (
              <>
                <button onClick={() => updateStatus(l._id, 'Approved')}>Approve</button>
                <button onClick={() => updateStatus(l._id, 'Rejected')}>Reject</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
