import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { applyLeave } from '../../features/emp/leaves/leaveSlice';

const LeaveForm = ({ token }) => {
  const [form, setForm] = useState({ from: '', to: '', reason: '' });
  const dispatch = useDispatch();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(applyLeave({ data: form, token }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Apply for Leave</h3>
      <input name="from" type="date" onChange={handleChange} required />
      <input name="to" type="date" onChange={handleChange} required />
      <input name="reason" placeholder="Reason" onChange={handleChange} required />
      <button type="submit">Apply</button>
    </form>
  );
};

export default LeaveForm;
